import { InputNumber } from 'antd'

const currencyFormatter = value => {
  return new Intl.NumberFormat('pt-BR', {
    style: 'currency',
    currency: 'BRL'
  }).format(value);
};

const currencyParser = val => {
  if (typeof val === "string" && !val.length)
    val = "0,0";

  val.replace('.', '')
  val.replace(',', '.');

  return val
