  //   reversedVal = reversedVal.replace(new RegExp("\\" + decimal, "g"), ".");

  //   return Number.isNaN(reversedVal) ? 0 : reversedVal;
  // } catch (error) {
  //   console.error(error);
  // }
};

const CurrencyInput = () => {
  return (
    <InputNumber
      defaultValue={150}
      style={{
        width: 400,
        marginLeft: "1rem"
      }}
      formatter={currencyFormatter}
      parser={currencyParser}
    />
  );
};

export default CurrencyInput
